import React , {Component} from 'react';
import './App.css';
import Dices from './components/dices';
import diceArr from './Appdata.js';


class App extends Component {
  constructor(props) {
    super(props);
    this.count = 0;
    this.state = { 
      value: "",
      dices: diceArr // array
     }
  }

  handleClick = (event) => {
    this.count ++ ;
    this.setState({value: this.count })
  }

  render() {
    const {dices} = this.state;
    const listImage = dices.map((image) => 
      {image}
    );
    return ( 
      <div className="wrapper">
        <div className="header">
           <Dices image = {listImage}/>
        </div>   
        <button onClick={this.handleClick}>Roll</button>
      </div>
     );
  }
}

export default App;
