import React , {Component} from 'react';
import './App.css';
import Dices from './components/dices';
import diceArr from './Appdata.js';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = { 
      count: 0,
      dices: diceArr // array
     }
  }

  handleClick = (e) => {
    this.count ++;
    this.setState({value: this.count})
  }

  render() {
    const MAX_OF_ROLLING = 6
    const {dices} = this.state;
    const {count} = this.state;
    //const {value} = this.state;

    if(count === MAX_OF_ROLLING){
      this.setState({count: 0})
    }


    return ( 
      <div className="wrapper">

        <div className="header">
          <Dices  image={dices[count]}/>
           <Dices  image={dices[0]}/>
        </div>  

        <div className="main">
          <button className="btn" onClick={this.handleClick}>Roll</button>
        </div> 

      </div>
     );
  }
}

export default App;
