import React , {Component} from 'react';
import './App.css';
import Dices from './components/dices';
import diceArr from './Appdata.js';

const getRandomInt = (max) => {
    return Math.floor(Math.random() * Math.floor(max));
}


class App extends Component {
  constructor(props) {
    super(props);

    this.state = { 
      count: 0,
      dices: diceArr // array
     }
  }

  handleClick = (e) => {
    this.setState({count: this.state.count + 1})
  }

  render() {
    const MAX_OF_ROLLING = 6

    const {dices} = this.state;
    const {count} = this.state;

    //if user roll at at the end of image
    // set count state to 0.
    if(count === MAX_OF_ROLLING){
      this.setState({count: 0})
    }


    return ( 
      <div className="wrapper">

        <div className="header">
          <Dices  image={dices[getRandomInt(3)]}/>
           <Dices  image={dices[getRandomInt(2)]}/>
        </div>  

        <div className="main">
          <button className="btn" onClick={this.handleClick}>Roll</button>
        </div> 

      </div>
     );
  }
}

export default App;
