import React , {Component} from 'react';
import './App.css';
import Dices from './components/dices';
import diceArr from './Appdata.js';
import diceeLogo from './assets/logo/vegaslogo.png';
import ReactAudioPlayer from 'react-audio-player';
import blackpink from './audio/blackpink.mp3';


const getRandomInt = (max) => {
    return Math.floor(Math.random() * Math.floor(max));
}
const url = "https://www.khjournals.com/dice/roll/"
  let config =  {
    method: "GET",
    headers: {
      "Content-type": "application/json",

    },
  };


class App extends Component {
  constructor(props) {
    super(props);

    this.state = { 
      count: 0,
      dices: diceArr, // array
      roll: "",

     }
     this.audio = new Audio(blackpink);
     console.log("1");
  }

  handleClick = (e) => {
    this.setState({count: this.state.count + 1})
  }

  fetch = () => {
          fetch(url, config)
          .then(response => response.json())
          .then(data => {
            //access the end of data length
            this.setState({roll: data[data.length-1].roll});
          })
          .catch(error => {
            console.error('Error:', error);
          });
  }

  componentDidMount(){
    this.interval =  setInterval(this.fetch, 3000);
  }

  handlePause = () => {
    this.audio.play();

    console.log("pause");
    // this.rap.audioEl.onPause();
    // console.log("pause");
  }
  render() {
    const {dices, roll} = this.state;



    console.log("3");
    return ( 
      <div className="wrapper">  

        <div className="header">

          <div className="welcome-to-vegas">
            <img src= {diceeLogo} alt="vegas" />
          </div>

          <div className="dices">
            <Dices  image={dices[getRandomInt(6)]}/>
            <Dices  image={dices[getRandomInt(6)]}/>
          </div>  

          <div className="main">
            {roll == "roll"
             ? <button className="btn" onClick={this.handleClick}>Roll</button>
             : <button className="btn">Roll</button>
            }
          </div>

          <button className="btn" onClick={this.handlePause("play")}>Roll</button>

        </div>




      </div>
     );
  }
}

export default App;
