import React , {Component} from 'react';
import './App.css';
import Dices from './components/dices';

class App extends Component {
  constructor(props) {
    super(props);
    this.count = 0;
    this.state = { 
      value: ""
     }
  }

  handleClick = (event) => {
    this.count ++ ;
    this.setState({value: this.count })

  }


  render() {
    return ( 
      <div className="wrapper">   
        <Dices />
        <button onClick={this.handleClick}>Roll</button>
      </div>
     );
  }
}

export default App;
